      function parse(cookie)
      {
      var list_final = []
      for (i = 0; i < cookie.length; i++)
      {
      var each_cookie = cookie[i]
      for (j = 0; j < each_cookie.length; j++)
      {
      var current_c = each_cookie[j]
      var domain = current_c.domain
      if (domain == ".aliexpress.com")
      {
      list_final.push(current_c)
      }
      list_final.push(current_c)
      }
      }
      VAR_NEW_COOKIE = JSON.stringify(list_final)
      VAR_SAVED_COOKIES = '{"cookies":'+ VAR_NEW_COOKIE + '}'
      }
      http_client_restore_cookies(VAR_SAVED_COOKIES)  //загружаем куки в HTTP клиент
      VALUE = http_client_save_cookies()              //загружаем куки в браузер
      COOKIES = ""
      if(VALUE.length > 0)
      {
      COOKIES = JSON.parse(VALUE)["cookies"]
      if(typeof(COOKIES) == "undefined")
      COOKIES = ""
      else
      COOKIES = JSON.stringify(COOKIES)
      }
      restore_cookies(COOKIES)!
