         function shopping_cart_id(response, prepare)
         {
         var response_for_price = JSON.parse(response);
         var response = JSON.parse(response).stores;
         VAR_GOODS_SHOPPING_CART_ID = []
         for (i = 0; i < response.length; i++)
         {
         var storelist = response[i].storeList
