         {
         var products = storelist[j].products
         for (k = 0; k < products.length; k++)
         {
         var itemId = products[k].itemId
         var productId = products[k].productId
         productId = productId + ""
         if (VAR_GOODS_ITEM_ID_LIST.indexOf(productId) != -1)
         {
         var title = VAR_GOODS_TITLE = products[k].title
         var count = VAR_ALL_COUNT= products[k].count
         var goods_amount = VAR_GOODS_AMOUNT = products[k].cost.price.amount
         var goods_currency = VAR_GOODS_CURRENCY = products[k].cost.price.currency
         log("Ответ корзины: ID товара в корзине - " + itemId + " . Стоимость: " + count + " x " + goods_amount + " " + goods_currency + " ( " + title + " )")
         VAR_GOODS_SHOPPING_CART_ID.push(itemId)
         }
         else{
         fail("Товар не найден в корзине.", false)
         }
         }
         }
         }
         try{
         var total_cost = response_for_price.price["orderCharges"][0].charges[0].cost
         }
         catch (e)
         {
         total_cost = "";
         }
         if (prepare == false){
         log("Общая стоимость товаров в корзине: " + total_cost + " " + goods_currency)
         }
