         function parse(cookie)
         {
         for (i = 0; i < cookie.length; i++)
         {
         var domain = cookie[i].domain
         if (domain == ".aliexpress.com")
         {
         var value = cookie[i].value.split("&")
         for (j = 0; j < value.length; j++)
         {
         if (value[j].split("=")[0] == "x_csrf")
         {
         VAR_CSRF = value[j].split("=")[1]
         }
         }
         }
         else if (domain == ".aliexpress.ru")
         {
         var value = cookie[i].value.split("&")
         for (j = 0; j < value.length; j++)
         {
         if (value[j].split("=")[0] == "x_csrf")
         {
         VAR_CSRF_RU = value[j].split("=")[1]
         }
         }
         }
         }
         }
         function randomString(i) // функция рандомной строки
         {
         var text = "";
         var possible = "abcdefghijklmnopqrstuvwxyz";
         while (text.length < i)
         text += possible.charAt(Math.floor(Math.random() * possible.length));
         return text;
         }
         function randomUpperLit() //функция рандомнй прописной буквы
         {
         var text = "";
         var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
         for (var i = 0; i < 1; i++)
         text += possible.charAt(Math.floor(Math.random() * possible.length));
         return text;
         }
         function randnumb(min, max) // функция рандомного целого числа
         {
         return Math.round(Math.random() * (max - min)) + min;
         }
         function namesecond() {
         var i = Math.round(Math.random());
         if (i == 1) {
         name = "";
         } else {
         name = randomUpperLit() + randomString(randnumb(3, 9));
         }
         return name;
         }
         VAR_NAME = randomUpperLit() + randomString(randnumb(7,15)) + " "+ randomUpperLit() + randomString(randnumb(6,16)) + " " + namesecond(); //рандомное ФИО
