         http_client_post("https://shoppingcart.aliexpress.com/order/placeOrder4Js.htm?purchaseId=" + VAR_SHOPPING_CART_IDS_STRING + "\u0026shopcartId=" + VAR_SHOPPING_CART_IDS_STRING, ["data", "aeOrderFrom=main_shopcart\u0026availableProductShopcartIds=" + VAR_SHOPPING_CART_IDS_STRING + "\u0026addressId=" + VAR_ADDRESSID + "\u0026payMethod=CASH_IN\u0026paymentGateway=aepay\u0026paymentOption=CASH_IN\u0026subPaymentOption=CASH_IN\u0026subPayMethod=CASH_IN\u0026isUsePlatformCouponId=true\u0026messageToSupplier-item-" + VAR_LAST_CART_ID + "=" + message + "\u0026coupon-item=" + VAR_COUPON_ID], { "content-type": "custom/" + ("application/x-www-form-urlencoded"), "encoding": ("UTF-8"), "method": ("POST"), headers: ("") })!
         })!
         })!
         _if(!_cycle_params().if_else,function(){
         log("оформляем в USD");
         http_client_post("https://shoppingcart.aliexpress.com/order/placeOrder4Js.htm?purchaseId=" + VAR_SHOPPING_CART_IDS_STRING + "\u0026shopcartId=" + VAR_SHOPPING_CART_IDS_STRING, ["data", "aeOrderFrom=main_shopcart\u0026availableProductShopcartIds=" + VAR_SHOPPING_CART_IDS_STRING + "\u0026addressId=" + VAR_ADDRESSID + "\u0026payMethod=WM_EBANK\u0026paymentGateway=aepay\u0026paymentOption=WM_EBANK\u0026subPaymentOption=WM_EBANK\u0026subPayMethod=WM_EBANK\u0026isUsePlatformCouponId=true\u0026messageToSupplier-item-" + VAR_LAST_CART_ID + "=" + message + "\u0026coupon-item=" + VAR_COUPON_ID], { "content-type": "custom/" + ("application/x-www-form-urlencoded"), "encoding": ("UTF-8"), "method": ("POST"), headers: ("") })!
         })!
         delete _cycle_params().if_else;
         // без купона рабочий вар.  http_client_post("https://shoppingcart.aliexpress.com/order/placeOrder4Js.htm", ["objectId",VAR_GOODS_ITEM_ID,"quantity",VAR_GOODS_QUALITY,"skuAttr",VAR_GOODS_SKU_ATTR,"shippingCompany",VAR_GOODS_DELIVERY,"_csrf_token_",VAR_CSRF,"addressId",VAR_ADDRESSID,"messageToSupplier-item-"+VAR_GOODS_ITEM_ID,message,"payMethod","WM_EBANK"], {"content-type":("urlencode"), "encoding":("UTF-8"), "method":("POST"),headers:("")})!
         // рабочий вариант на 1 товар http_client_post("https://shoppingcart.aliexpress.com/order/placeOrder4Js.htm", ["objectId",VAR_GOODS_ITEM_ID,"quantity",VAR_GOODS_QUALITY,"skuAttr",VAR_GOODS_SKU_ATTR,"shippingCompany",VAR_GOODS_DELIVERY,"_csrf_token_",VAR_CSRF,"addressId",VAR_ADDRESSID,"messageToSupplier-item-"+VAR_GOODS_ITEM_ID,message,"payMethod","WM_EBANK","coupon-item",VAR_COUPON_ID], {"content-type":("urlencode"), "encoding":("UTF-8"), "method":("POST"),headers:("")})!// это запрос заказа без корзины
         // тестовый вариант на несколько товаров http_client_post("https://shoppingcart.aliexpress.com/order/placeOrder4Js.htm", ["objectId",VAR_SHOPPING_CART_IDS_STRING,"quantity",VAR_GOODS_QUALITY,"skuAttr",VAR_GOODS_SKU_ATTR,"shippingCompany",VAR_GOODS_DELIVERY,"_csrf_token_",VAR_CSRF,"addressId",VAR_ADDRESSID,"messageToSupplier-item-"+VAR_GOODS_ITEM_ID,message,"payMethod","WM_EBANK","coupon-item",VAR_COUPON_ID], {"content-type":("urlencode"), "encoding":("UTF-8"), "method":("POST"),headers:("")})!// это запрос заказа без корзины
         //--Парсим ответ--
         var response = JSON.parse(http_client_encoded_content("auto").replace("null(", "").replace(");", ""));
         var result = response.result
         _cycle_params().if_else = result == "success";
         _if(_cycle_params().if_else, function () {
         var orderIds = VAR_ORDERIDS = response.orderIds
         log("Заказ успешно оформлен, номер заказа: " + orderIds)
         sleep(8000)!
         })!
         _if(!_cycle_params().if_else, function () {
         reason = "Ошибка при заказе"
         save(VAR_CURRENT_ACCOUNT, reason)
         fail("Ошибка при создании заказа: " + http_client_encoded_content("auto").replace("null(", "").replace(");", ""), false)
         })!
         delete _cycle_params().if_else;
         //=Парсим список заказов и их статусов=
         log("Получили список заказов, ищем созданные...")
         http_client_get2("https://m.aliexpress.com/api/user/orders?currentPage=1\u0026pageSize=20\u0026timeZoneOffsetOfMinutes=-180", { method: ("GET"), headers: ("referer: https://m.aliexpress.com/orderlist.html") })!
         response = http_client_encoded_content("auto")
         var orderList = JSON.parse(response).data.orderList
         var orderIds = VAR_ORDERIDS;
         for (j = 0; j < orderIds.length; j++) {
         var order = orderIds[j]
         for (i = 0; i < orderIds.length; i++) {
         var orderId = orderList[i].orderId
         if (orderId == order) {
         var orderStatus = orderList[i].showStatus
         var orderPriceFull = orderList[i].subList[0].productBuyerLocalPrice.value
         var orderPricePay = orderList[i].orderAmount.formatedAmount
         var productName = orderList[i].subList[0].productName
         var orderIdMain = orderList[i].orderId
         if (orderStatus == "aeWaitPaymentOrders") {
         reason = "Ожидание оплаты"
         save(VAR_CURRENT_ACCOUNT, reason, "\nЗаказ № " + orderIds + ", " + "\n" + productName)
         log("Заказ № " + orderIds + " ожидает оплаты:" + orderPricePay + " " + productName)
         }
         else {
         reason = "Заказ закрыт"
         save("\n" + reason, "\n" + VAR_CURRENT_ACCOUNT + " Заказ № " + orderIds, productName)
         log("Заказ закрыт")
         }
         }
         }
         }
         section_end()!

      })!
      section_end()!

   })!
   section_end()!

